    // Setters

    /**
     * @test
     * @dataProvider badgeNameSuccessProvider
     * @throws Exception
     */
    public function setBadgeNameSuccess(string $name)
    {
        $badge = Badge::addBadge($this->courseId, "Badge", "Perform action", false, false, false, false, false, [
            ["description" => "one time", "goal" => 1, "reward" => 100],
            ["description" => "two times", "goal" => 2, "reward" => 100],
            ["description" => "three times", "goal" => 3, "reward" => 100]
        ]);
        $badge->setName($name);
        $this->assertEquals($name, $badge->getName());

        $this->assertTrue(file_exists($badge->getDataFolder(true, $name)));
        $this->assertFalse(file_exists($badge->getDataFolder(true, "Badge")));

        $this->assertEquals($name, $badge->getRule()->getName());
        $this->assertEquals("award_badge(target, \"" . $name . "\", lvl)", $badge->getRule()->getThen());
    }

    /**
     * @test
     * @dataProvider badgeNameFailureProvider
     * @throws Exception
     */
    public function setBadgeNameFailure($name)
    {
        $badge = Badge::addBadge($this->courseId, "Badge", "Perform action", false, false, false, false, false, [
            ["description" => "one time", "goal" => 1, "reward" => 100],
            ["description" => "two times", "goal" => 2, "reward" => 100],
            ["description" => "three times", "goal" => 3, "reward" => 100]
        ]);
        try {
            $badge->setName($name);
            $this->fail("Error should have been thrown on 'setBadgeNameFailure'");

        } catch (Exception|TypeError $e) {
            $this->assertEquals("Badge", $badge->getName());
            $this->assertTrue(file_exists($badge->getDataFolder(true, "Badge")));
            $this->assertEquals("Badge", $badge->getRule()->getName());
            $this->assertEquals("award_badge(target, \"Badge\", lvl)", $badge->getRule()->getThen());
        }
    }

    /**
     * @test
     * @throws Exception
     */
    public function setBadgeNameDuplicateName()
    {
        $badge1 = Badge::addBadge($this->courseId, "Badge1", "Perform action", false, false, false, false, false, [
            ["description" => "one time", "goal" => 1, "reward" => 100],
            ["description" => "two times", "goal" => 2, "reward" => 100],
            ["description" => "three times", "goal" => 3, "reward" => 100]
        ]);
        $badge2 = Badge::addBadge($this->courseId, "Badge2", "Perform action", false, false, false, false, false, [
            ["description" => "one time", "goal" => 1, "reward" => 100],
            ["description" => "two times", "goal" => 2, "reward" => 100],
            ["description" => "three times", "goal" => 3, "reward" => 100]
        ]);
        try {
            $badge2->setName($badge1->getName());
            $this->fail("Error should have been thrown on 'setBadgeDuplicateName'");

        } catch (Exception|TypeError $e) {
            $this->assertEquals("Badge2", $badge2->getName());
            $this->assertTrue(file_exists($badge2->getDataFolder(true, "Badge2")));
            $this->assertEquals("Badge2", $badge2->getRule()->getName());
