    // Badge Manipulation

    /**
     * @test
     * @dataProvider badgeSuccessProvider
     * @throws Exception
     */
    public function addBadgeSuccess(string $name, string $description, bool $isExtra, bool $isBragging, bool $isCount,
                                    bool $isPost, bool $isPoint, array $levels)
    {
        $badge = Badge::addBadge($this->courseId, $name, $description, $isExtra, $isBragging, $isCount, $isPost, $isPoint, $levels);

        // Check is added to database
        $badgeDB = Badge::parse(Core::database()->select(Badge::TABLE_BADGE, ["id" => $badge->getId()]));
        $this->assertEquals($badge->getData(), $badgeDB);

        // Check levels
        $lvls = $badge->getLevels();
        $this->assertSameSize($levels, $lvls);

        $size = count($levels);
        $lvl1 = $lvls[0];
        $this->assertEquals($levels[0]["description"], $lvl1["description"]);
        $this->assertEquals($levels[0]["goal"], $lvl1["goal"]);
        $this->assertEquals($levels[0]["reward"], $lvl1["reward"]);

        if ($size >= 2) {
            $lvl2 = $lvls[1];
            $this->assertEquals($levels[1]["description"], $lvl2["description"]);
            $this->assertEquals($levels[1]["goal"], $lvl2["goal"]);
            $this->assertEquals($levels[1]["reward"], $lvl2["reward"]);
        }

        if ($size == 3) {
            $lvl3 = $lvls[2];
            $this->assertEquals($levels[2]["description"], $lvl3["description"]);
            $this->assertEquals($levels[2]["goal"], $lvl3["goal"]);
            $this->assertEquals($levels[2]["reward"], $lvl3["reward"]);
        }

        // Check data folder was created
        $this->assertTrue(file_exists($badge->getDataFolder()));

        // Check rule was created
        $rule = $badge->getRule();
        $this->assertTrue($rule->exists());
        $this->assertEquals($this->trim("rule: $name
tags: 
# $description
#	lvl.1: " . $lvl1["description"] . ($size >= 2 ? "\n#\tlvl.2: " . $lvl2["description"] : "") . ($size == 3 ? "\n#\tlvl.3: " . $lvl3["description"] : "") . "

	when:
		progress = None # TODO: get student progress in badge
		
		# Compute badge level the student deserves
		lvl = compute_lvl(progress, " . $lvl1["goal"] . ($size >= 2 ? ", " . $lvl2["goal"] : "") . ($size == 3 ? ", " . $lvl3["goal"] : "") . ")

	then:
		award_badge(target, \"$name\", lvl)"), $this->trim($rule->getText()));
    }

