                                     bool $isPost, bool $isPoint, array $levels)
    {
        $badge = Badge::addBadge($this->courseId, "NAME", "DESCRIPTION", false, false, false, false, false, [
            ["description" => "DESC1", "goal" => 0, "reward" => 100],
            ["description" => "DESC2", "goal" => 1, "reward" => 100],
