        ]);
        $badge->editBadge($name, $description, $isExtra, $isBragging, $isCount, $isPost, $isPoint, true, $levels);

        // Check is updated
        $this->assertEquals($name, $badge->getName());
        $this->assertEquals($description, $badge->getDescription());
        $this->assertEquals($isExtra, $badge->isExtra());
        $this->assertEquals($isBragging, $badge->isBragging());
        $this->assertEquals($isCount, $badge->isCount());
        $this->assertEquals($isPost, $badge->isPost());
        $this->assertEquals($isPoint, $badge->isPoint());
        $this->assertTrue($badge->isActive());

        // Check levels
        $lvls = $badge->getLevels();
        $this->assertSameSize($levels, $lvls);

        $size = count($levels);
        $lvl1 = $lvls[0];
        $this->assertEquals($levels[0]["description"], $lvl1["description"]);
        $this->assertEquals($levels[0]["goal"], $lvl1["goal"]);
        $this->assertEquals($levels[0]["reward"], $lvl1["reward"]);

        if ($size >= 2) {
            $lvl2 = $lvls[1];
            $this->assertEquals($levels[1]["description"], $lvl2["description"]);
            $this->assertEquals($levels[1]["goal"], $lvl2["goal"]);
            $this->assertEquals($levels[1]["reward"], $lvl2["reward"]);
        }

        if ($size == 3) {
            $lvl3 = $lvls[2];
            $this->assertEquals($levels[2]["description"], $lvl3["description"]);
            $this->assertEquals($levels[2]["goal"], $lvl3["goal"]);
            $this->assertEquals($levels[2]["reward"], $lvl3["reward"]);
        }

        // Check data folder
        $this->assertTrue(file_exists($badge->getDataFolder(true, $name)));

        // Check rule
        $this->assertEquals($this->trim("rule: $name
