    /**
     * @test
     * @throws Exception
     */
    public function deleteBadge()
    {
        $badge1 = Badge::addBadge($this->courseId, "Badge1", "Perform action", false, false, false, false, false, [
            ["description" => "one time", "goal" => 1, "reward" => 100],
            ["description" => "two times", "goal" => 2, "reward" => 100],
            ["description" => "three times", "goal" => 3, "reward" => 100]
        ]);
        $badge2 = Badge::addBadge($this->courseId, "Badge2", "Perform action", false, false, false, false, false, [
            ["description" => "one time", "goal" => 1, "reward" => 100],
            ["description" => "two times", "goal" => 2, "reward" => 100],
            ["description" => "three times", "goal" => 3, "reward" => 100]
        ]);

        // Not empty
        Badge::deleteBadge($badge2->getId());
        $this->assertCount(1, Badge::getBadges($this->courseId));
        $this->assertFalse(file_exists($badge2->getDataFolder(true, "Badge2")));
        $this->assertCount(1, Section::getSectionByName($this->courseId, Badges::RULE_SECTION)->getRules());

        // Empty
        Badge::deleteBadge($badge1->getId());
        $this->assertEmpty(Badge::getBadges($this->courseId));
        $this->assertFalse(file_exists($badge2->getDataFolder(true, "Badge1")));
        $this->assertEmpty(Section::getSectionByName($this->courseId, Badges::RULE_SECTION)->getRules());
    }

