    // Levels

    /**
     * @test
     * @throws Exception
     */
    public function getLevels()
    {
        $badge = Badge::addBadge($this->courseId, "Badge", "Perform action", false, false, false, false, false, [
            ["description" => "one time", "goal" => 1, "reward" => 100],
            ["description" => "two times", "goal" => 2, "reward" => 100],
            ["description" => "three times", "goal" => 3, "reward" => 100]
        ]);
        $levels = $badge->getLevels();
        $this->assertIsArray($levels);
        $this->assertCount(3, $levels);

        $keys = ["id", "number", "description", "goal", "reward"];
        $nrKeys = count($keys);

        $lvl1 = $levels[0];
        $this->assertCount($nrKeys, $lvl1);
        $this->assertArrayHasKey("id", $lvl1);
        $this->assertArrayHasKey("number", $lvl1);
        $this->assertArrayHasKey("description", $lvl1);
        $this->assertArrayHasKey("goal", $lvl1);
        $this->assertArrayHasKey("reward", $lvl1);
        $this->assertEquals(1, $lvl1["number"]);
        $this->assertEquals("one time", $lvl1["description"]);
        $this->assertEquals(1, $lvl1["goal"]);
        $this->assertEquals(100, $lvl1["reward"]);

        $lvl2 = $levels[1];
        $this->assertCount($nrKeys, $lvl2);
        $this->assertArrayHasKey("id", $lvl2);
        $this->assertArrayHasKey("number", $lvl2);
        $this->assertArrayHasKey("description", $lvl2);
        $this->assertArrayHasKey("goal", $lvl2);
        $this->assertArrayHasKey("reward", $lvl2);
        $this->assertEquals(2, $lvl2["number"]);
        $this->assertEquals("two times", $lvl2["description"]);
        $this->assertEquals(2, $lvl2["goal"]);
        $this->assertEquals(100, $lvl2["reward"]);

        $lvl3 = $levels[2];
        $this->assertCount($nrKeys, $lvl3);
        $this->assertArrayHasKey("id", $lvl3);
        $this->assertArrayHasKey("number", $lvl3);
        $this->assertArrayHasKey("description", $lvl3);
        $this->assertArrayHasKey("goal", $lvl3);
        $this->assertArrayHasKey("reward", $lvl3);
        $this->assertEquals(3, $lvl3["number"]);
        $this->assertEquals("three times", $lvl3["description"]);
        $this->assertEquals(3, $lvl3["goal"]);
        $this->assertEquals(100, $lvl3["reward"]);
    }

    /**
     * @test
     * @throws Exception
     */
    public function setLevels()
    {
        $badge = Badge::addBadge($this->courseId, "Badge", "Perform action", false, false, false, false, false, [
            ["description" => "one time", "goal" => 1, "reward" => 100],
            ["description" => "two times", "goal" => 2, "reward" => 100],
            ["description" => "three times", "goal" => 3, "reward" => 100]
        ]);

        // One level
        $badge->setLevels([
            ["description" => "one time", "goal" => 1, "reward" => 100]
        ]);
        $this->assertCount(1, $badge->getLevels());

        // Two levels
        $badge->setLevels([
            ["description" => "one time", "goal" => 1, "reward" => 100],
            ["description" => "two times", "goal" => 2, "reward" => 100]
        ]);
        $this->assertCount(2, $badge->getLevels());

        // Three levels
        $badge->setLevels([
            ["description" => "one time", "goal" => 1, "reward" => 100],
            ["description" => "two times", "goal" => 2, "reward" => 100],
            ["description" => "three times", "goal" => 3, "reward" => 100]
        ]);
        $this->assertCount(3, $badge->getLevels());
    }


    // Rules

    /**
     * @test
     * @dataProvider badgeSuccessProvider
     * @throws Exception
     */
    public function generateRuleParamsFresh(string $name, string $description, bool $isExtra, bool $isBragging, bool $isCount,
                                            bool $isPost, bool $isPoint, array $levels)
    {
        $params = Badge::generateRuleParams($name, $description, $levels);

        // Name
        $this->assertTrue(isset($params["name"]));
        $this->assertEquals($name, $params["name"]);

        // Description
        $this->assertTrue(isset($params["description"]));
        $this->assertEquals($description . "\n\tlvl.1: " . $levels[0]["description"] .
            (count($levels) >= 2 ? "\n\tlvl.2: " . $levels[1]["description"] : "") .
            (count($levels) == 3 ? "\n\tlvl.3: " . $levels[2]["description"] : ""), $params["description"]);

        // When
        $this->assertTrue(isset($params["when"]));
        $this->assertEquals("progress = None # TODO: get student progress in badge

# Compute badge level the student deserves
lvl = compute_lvl(progress, " . $levels[0]["goal"] . (count($levels) >= 2 ? ", " . $levels[1]["goal"] : "") .
            (count($levels)== 3 ? ", " . $levels[2]["goal"] : "") . ")", $params["when"]);

        // Then
        $this->assertTrue(isset($params["then"]));
        $this->assertEquals("award_badge(target, \"$name\", lvl)", $params["then"]);
    }

    /**
     * @test
     * @dataProvider badgeSuccessProvider
     * @throws Exception
     */
    public function generateRuleParamsNotFresh(string $name, string $description, bool $isExtra, bool $isBragging, bool $isCount,
                                               bool $isPost, bool $isPoint, array $levels)
    {
        // Given
        $description = $description . "\n\tlvl.1: " . $levels[0]["description"] .
            (count($levels) >= 2 ? "\n\tlvl.2: " . $levels[1]["description"] : "") .
            (count($levels) == 3 ? "\n\tlvl.3: " . $levels[2]["description"] : "");
        $when = "progress = None # Some changes

# Compute badge level the student deserves
lvl = compute_lvl(progress, " . $levels[0]["goal"] . (count($levels) >= 2 ? ", " . $levels[1]["goal"] : "") .
            (count($levels)== 3 ? ", " . $levels[2]["goal"] : "") . ")";
        $then = "# Some changes
award_badge(target, \"$name\", lvl)";

        $rule = Section::getSectionByName($this->courseId, Badges::RULE_SECTION)->addRule($name, $description, $when, $then);

        // When
        $params = Badge::generateRuleParams("New Name", "New Description", [
            ["description" => "new", "goal" => 100, "reward" => 1000]
        ], false, $rule->getId());

        // Then

        // Name
        $this->assertTrue(isset($params["name"]));
        $this->assertEquals("New Name", $params["name"]);

        // Description
        $this->assertTrue(isset($params["description"]));
        $this->assertEquals("New Description\n\tlvl.1: new", $params["description"]);

        // When
        $this->assertTrue(isset($params["when"]));
        $this->assertEquals("progress = None # Some changes

# Compute badge level the student deserves
lvl = compute_lvl(progress, 100)", $params["when"]);

        // Then
        $this->assertTrue(isset($params["then"]));
        $this->assertEquals("# Some changes
award_badge(target, \"New Name\", lvl)", $params["then"]);
    }


