     * @test
     */
    public function getVirtualCurrencyName()
    {
        $this->assertEquals("Token(s)", $this->module->getName());
    }

    /**
     * @test
     * @throws Exception
     */
    public function setVirtualCurrencyName()
    {
        $this->module->setName("Gold");
        $this->assertEquals("Gold", $this->module->getName());
    }


    /**
     * @test
     * @throws Exception
     */
