    {
        file_put_contents($this->module->getDataFolder() . "/token.png", "");
        $this->assertEquals(API_URL . "/" . $this->course->getDataFolder(false) . "/" .
            $this->module->getDataFolder(false) . "/token.png", $this->module->getImage());
    }

    /**
     * @test
     * @throws Exception
     */
