    /**
     * @test
     * @dataProvider imageProvider
     * @throws Exception
     */
    public function setImage(string $base64)
    {
        $this->module->setImage($base64);
        $this->assertTrue($this->module->hasImage());
    }

    /**
     * @test
     * @throws Exception
     */
    public function hasImage()
    {
        // Image not set
        $this->assertFalse($this->module->hasImage());

        // Image set
        file_put_contents($this->module->getDataFolder() . "/token.png", "");
        $this->assertTrue($this->module->hasImage());
