    // Wallet

    /**
     * @test
     * @throws Exception
     */
    public function getUserTokens()
    {
        // Given
        $students = $this->course->getStudents();
        $student1 = $this->course->getCourseUserById($students[0]["id"]);
        $student2 = $this->course->getCourseUserById($students[1]["id"]);
        $this->module->setUserTokens($student2->getId(), 1000);

        // Then
        $this->assertEquals(0, $this->module->getUserTokens($student1->getId()));
        $this->assertEquals(1000, $this->module->getUserTokens($student2->getId()));
    }

    /**
     * @test
     * @throws Exception
     */
    public function setUserTokens()
    {
        // Given
        $student = $this->course->getCourseUserById($this->course->getStudents()[0]["id"]);

        // Wallet initialized
        $this->module->setUserTokens($student->getId(), 1000);
        $this->assertEquals(1000, $this->module->getUserTokens($student->getId()));

        // Wallet not initialized
        Core::database()->delete(VirtualCurrency::TABLE_WALLET, ["user" => $student->getId()]);
        try {
            $this->module->setUserTokens($student->getId(), 2000);
        } catch (Exception $e) {
            $this->assertFalse($this->module->userHasWallet($student->getId()));
        }
    }

    /**
     * @test
     * @throws Exception
     */
    public function updateUserTokens()
    {
        // Given
        $student = $this->course->getCourseUserById($this->course->getStudents()[0]["id"]);

        // Add XP
        $this->module->updateUserTokens($student->getId(), 1000);
        $this->assertEquals(1000, $this->module->getUserTokens($student->getId()));

        // Subtract XP
        $this->module->updateUserTokens($student->getId(), -500);
        $this->assertEquals(500, $this->module->getUserTokens($student->getId()));

        // Do nothing
        $this->module->updateUserTokens($student->getId(), 0);
        $this->assertEquals(500, $this->module->getUserTokens($student->getId()));
    }

    /**
     * @test
     * @throws Exception
     */
    public function userHasWallet()
    {
        // Given
        $student = $this->course->getCourseUserById($this->course->getStudents()[0]["id"]);

        // Has XP
        $this->assertTrue($this->module->userHasWallet($student->getId()));

        // Doesn't have XP
        Core::database()->delete(VirtualCurrency::TABLE_WALLET, ["user" => $student->getId()]);
        $this->assertFalse($this->module->userHasWallet($student->getId()));
    }


    // Exchanging Tokens

    /**
     * @test
     * @throws Exception
     */
    public function exchangeTokensForXP()
    {
        // Given
        $xpLevels = new XPLevels($this->course);
        $xpLevels->setEnabled(true);
        $student = $this->course->getCourseUserById($this->course->getStudents()[0]["id"]);
        $this->module->setUserTokens($student->getId(), 1000);

        // When
        $earnedXP = $this->module->exchangeTokensForXP($student->getId());

        // Then
        $this->assertEquals(1000, $earnedXP);
        $this->assertEquals(1000, $xpLevels->getUserXP($student->getId()));
        $this->assertEquals(0, $this->module->getUserTokens($student->getId()));
        $this->assertNotEmpty((new Awards($this->course))->getUserAwards($student->getId()));
    }

    /**
     * @test
     * @throws Exception
     */
    public function exchangeTokensForXPWithRatio()
    {
        // Given
        $xpLevels = new XPLevels($this->course);
        $xpLevels->setEnabled(true);
        $student = $this->course->getCourseUserById($this->course->getStudents()[0]["id"]);
        $this->module->setUserTokens($student->getId(), 1000);

        // When
        $earnedXP = $this->module->exchangeTokensForXP($student->getId(), 0.5);

        // Then
        $this->assertEquals(500, $earnedXP);
        $this->assertEquals(500, $xpLevels->getUserXP($student->getId()));
        $this->assertEquals(0, $this->module->getUserTokens($student->getId()));
        $this->assertNotEmpty((new Awards($this->course))->getUserAwards($student->getId()));
    }

    /**
     * @test
     * @throws Exception
     */
    public function exchangeTokensForXPWithThreshold()
    {
        // Given
        $xpLevels = new XPLevels($this->course);
        $xpLevels->setEnabled(true);
        $student = $this->course->getCourseUserById($this->course->getStudents()[0]["id"]);
        $this->module->setUserTokens($student->getId(), 1000);

        // When
        $earnedXP = $this->module->exchangeTokensForXP($student->getId(), 0.5, 500);

        // Then
        $this->assertEquals(250, $earnedXP);
        $this->assertEquals(250, $xpLevels->getUserXP($student->getId()));
        $this->assertEquals(500, $this->module->getUserTokens($student->getId()));
        $this->assertNotEmpty((new Awards($this->course))->getUserAwards($student->getId()));
    }


    /**
     * @test
     * @throws Exception
     */
    public function hasExchanged()
    {
        // Given
        $xpLevels = new XPLevels($this->course);
        $xpLevels->setEnabled(true);
        $student = $this->course->getCourseUserById($this->course->getStudents()[0]["id"]);

        // When
        $this->module->exchangeTokensForXP($student->getId());

        // Then
        $this->assertTrue($this->module->hasExchanged($student->getId()));
    }

    /**
     * @test
     * @throws Exception
     */
    public function hasntExchanged()
    {
        // Given
        $xpLevels = new XPLevels($this->course);
        $xpLevels->setEnabled(true);
        $student = $this->course->getCourseUserById($this->course->getStudents()[0]["id"]);

        // Then
        $this->assertFalse($this->module->hasExchanged($student->getId()));
    }
